document.addEventListener('DOMContentLoaded', () => {
    const searchBar = document.querySelector('#search-bar');
    const submit = document.querySelector('button');

    submit.addEventListener('click', () => {
        const searchBarValue = searchBar.value;
        if (searchBarValue){
            getData(searchBarValue);
        }
        else{
            alert('Please write something before searching');
        }
        console.log(searchBar.value);
    })

    async function getData(location){
        try{
            const response = await fetch(`https://weather.visualcrossing.com/VisualCrossingWebServices/rest/services/timeline/${location}?key=WS375BAWWSUHW9NMLYR8XV42S`);
            const result = await response.json();
            separateData(result);
            console.log(result);
        }
        catch(error){
            console.log(error);
        }
    }


    const locationText = document.querySelector('#location');
    const conditionsImage = document.querySelector('img');
    const conditionsText = document.querySelector('#weather-descrip');
    const temperatureText = document.querySelector('#temp');
    const feelsLikeText = document.querySelector('#feels-like');
    function separateData(data){
        const address = data.resolvedAddress;
        const conditions = data.currentConditions.conditions;
        const temperature = data.currentConditions.temp;
        const feelsLike = data.currentConditions.feelslike;
        locationText.textContent = "Location: " + address;
        conditionsText.textContent = "Conditions: " + conditions;
        temperatureText.textContent = "Temperature in Farenheit: " + temperature;
        feelsLikeText.textContent = "Feels Like in Farenheit "+ feelsLike;
        switch (conditions){
            case "Clear":
                conditionsImage.src = "data:image/jpeg;base64,"
                break;
            case "Partially cloudy":
                conditionsImage.src = "data:image/png;base64,"
                break;
            case "Cloudy":
                conditionsImage.src = "data:image/png;base64,"
                break;
            case "Overcast":
                conditionsImage.src = "data:image/jpeg;base64,"
                break;
            case "Rain":
                conditionsImage.src = "data:image/png;base64,"
                break;
            case "Drizzle":
                conditionsImage.src = "data:image/jpeg;base64,"
                break;
            case "Snow":
                conditionsImage.src = "data:image/jpeg;base64,"
                break;
            case "Stormy":
                conditionsImage.src = "data:image/jpeg;base64,"
                break;
            case "Tornadoes":
                conditionsImage.src = "data:image/png;base64,"
                break;
            case "Fog":
                conditionsImage.src = "data:image/jpeg;base64,"
                break;
            default:
                alert('This type of weather is not found. Sorry :(');
                break;
    }

    }
});